ss='embed-responsive embed-responsive-16by9'><iframe class='embed-responsive-item' src='https://www.facebook.com/video/embed?video_id=". $json_a[$i]['object_id']."' allowfullscreen></iframe></div>";										echo "<div class='play'><i class='fa fa-play'></i></div>";										echo "<a href=".$json_a[$i]['link']." target='_blank' class='postlink'>";									}								} else {									/*instagram video else {									if ($json_a[$i]['media']!=null){										if($json_a[$i]['video']!=null){											echo "<div class='embed-responsive embed-responsive-16by9'><iframe class='embed-responsive-item' src='". $json_a[$i]['video']."' preload='none' allowfullscreen></iframe></div>";										}else{											echo "<img class='postimg' src='".$json_a[$i]['media']."'>";										}									}*/									if ($json_a[$i]['media']!=null) echo "<img class='postimg' src='".$json_a[$i]['media']."'>";								}?>																<?php 								if ($json_a[$i]['type'] == 'youtube') {									echo "<div class='embed-responsive embed-responsive-16by9'><iframe class='embed-responsive-item' src='". $json_a[$i]['video']."?rel=0&amp;showinfo=0' allowfullscreen></iframe></div>";									$testo=$json_a[$i]['title'];								}								?>																	<hr>								</a>							<p><?php echo $testo ?></p>							</div>					</div>					<div class="bot">						<p class="date"><i class="fa fa-clock-o"></i> <?php echo gmdate("d/M/Y - H:i",$json_a[$i]['ora'])?></p>						<!--<a href="http://www.<?php echo $json_a[$i]['type']?>.com" target="_blank"><p class="right"><i class="fa fa-<?php echo $json_a[$i]['type']; ?>"></i></p></a>-->					</div>				</div>			<?php } ?>				            </div>			        </div>        <?phpfunction autolink($string) {    $content_array = preg_split("/[\s]+/", $string);    $output = '';    foreach ($content_array as $content) {//starts with http://         if (substr($content, 0, 7) == "http://")            $content = '<a href="' . $content . '" target="_blank">' .  substr($content,7,20)  . '...</a>';					if (substr($content, 0, 8) == "https://")            $content = '<a href="' . $content . '" target="_blank">' .  substr($content,8,20)  . '...</a>';//starts with www.        if (substr($content, 0, 4) == "www.")            $content = '<a href="http://' . $content . '" target="_blank">' .  substr($content,4,20)  . '...</a>';        $output .= " " . $content;    }    $output = trim($output);    return $output;}?>    </body></html>